import React, { Fragment, useEffect, useState } from "react";

import { auth, db } from "../firebase";
import { collection, query, where, getDocs } from "firebase/firestore";

import { signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { async } from "@firebase/util";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'


const style: React.CSSProperties = {
    padding: '50px',
    background: '#FFF'
}

const ReservationCheckTop: React.FC = (props: any) => {
    const [reservations, setReservations] = useState([{ id: "", name: "" }]);
    useEffect(() => {
        const q = query(collection(db, "reservation"), where("number", "==", 1));
        fetchData();
        async function fetchData() {
            const querySnapshot = await getDocs(q);
            setReservations(
                querySnapshot.docs.map((doc) => ({ id: doc.id, name: doc.data().name }))
            );
        };
    }, []);

    return (
        <div style={style}>
            {reservations.map((reservation) => (<h3> {reservation.name}</h3>))}
            <Container>
                {/* Stack the columns on mobile by making one full-width and the other half-width */}
                <Row>
                    <Col xs={{ span: 2, offset: 2 }} md={{ span: 2, offset: 2 }}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={4} md={4}>
                        ×××
                    </Col>
                </Row>

                {/* Columns start at 50% wide on mobile and bump up to 33.3% wide on desktop */}
                <Row>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={{ span: 9, offset: 1 }} md={{ span: 9, offset: 1 }}>
                        ×××
                    </Col>
                </Row>

                {/* Columns are always 50% wide, on mobile and desktop */}
                <Row>
                    <Col xs={{ span: 2, offset: 2 }} md={{ span: 2, offset: 2 }}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                    <Col xs={2} md={2}>
                        <Button variant="outline-primary">Primary</Button>{' '}
                    </Col>
                </Row>
            </Container>
        </div>
    );
}

export default ReservationCheckTop;
